INSERT INTO JAVA_BD.movie (title, year, director, genre, synopsis) VALUES 
('Inception', 2010, 'Christopher Nolan', 'Ciencia ficción, Acción', 'Dom Cobb es un ladrón con la rara habilidad de entrar en los sueños de las personas y robarles sus secretos. Sin embargo, cuando le ofrecen borrar su pasado criminal, deberá lograr lo contrario: implantar una idea en la mente de una persona.');
INSERT INTO JAVA_BD.movie (title, year, director, genre, synopsis) VALUES 
('The Shawshank Redemption', 1994, 'Frank Darabont', 'Drama', 'Andy Dufresne, un banquero condenado por el asesinato de su esposa y su amante, forja una profunda amistad con Red, un preso veterano. A lo largo de los años, su optimismo e ingenio le ayudan a soportar la dura vida en prisión.');
INSERT INTO JAVA_BD.movie (title, year, director, genre, synopsis) VALUES 
('Pulp Fiction', 1994, 'Quentin Tarantino', 'Crimen, Drama', 'La película entrelaza varias historias de personajes en Los Ángeles, incluyendo la de dos sicarios, un boxeador y la esposa de un mafioso, en una narración no lineal que se convirtió en un icono del cine de los 90.');
INSERT INTO JAVA_BD.movie (title, year, director, genre, synopsis) VALUES 
('El Laberinto del Fauno', 2006, 'Guillermo del Toro', 'Fantasía, Drama', 'En la España franquista de 1944, una joven llamada Ofelia descubre un mundo subterráneo y fantástico donde se encuentra con criaturas misteriosas mientras lucha por sobrevivir en un mundo lleno de crueldad.');
INSERT INTO JAVA_BD.movie (title, year, director, genre, synopsis) VALUES 
('The Matrix', 1999, 'Lana y Lilly Wachowski', 'Ciencia ficción, Acción', 'Neo, un hacker, descubre que el mundo que conoce es una simulación creada por máquinas para mantener a la humanidad bajo control. Con la ayuda de un grupo de rebeldes, luchará por liberar a la humanidad.');
